import React, { useState } from 'react';
import ToDoList from './ToDoList';

const App = () =>{
  const[inputList,setInputList] = useState("");
  const[Items,setItems] = useState([]); // initial state is an empty array

  const itemEvent = (event) =>{
    setInputList(event.target.value)
  }

 const listOfItem = () =>{
  setItems((oldItems) => {

      return [...oldItems,inputList];
  });
 // type kr ke submit krne pr value ht jaye..
 // button ke liye alag hooks
  setInputList("");
 }

 const deleteItem = (id) =>{
  //  console.log("deleted")
  setItems((oldItems) =>{
    return oldItems.filter((arrElem,index) =>{
      // jiske id se mil raha hai  usko delete kra do
       return index !==id;
    } )
  })
}

  return(
    <>
      <div className="main_div">
        <div className="center_div">
        <br/>
        <h1>Todo List</h1>
        <br/>
        <input type="text" placeholder="Add a List"  onChange={itemEvent}
          value={inputList}
        />
        <button onClick={listOfItem}> + </button>

        <ol>
          {/* <li>{inputList}</li> */}
         {Items.map( (itemval,index) => {  // id key ke liye map function ke andar index
          return <ToDoList key={index} 
          id={index} 
          text={itemval}
            onSelect={deleteItem}
          />
         })}

        </ol>
        </div>
      </div>
    </>

  );
}


export default App;
