import React, { useState } from 'react';
import Header from './Header';
import Footer from './Footer';
import CreateNote from './CreateNote';
import Note from './Note'
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
const App = () => {

  const[addItem,setAddItem] = useState([]);

  const addNote = (note) =>{
    // alert("Thank you")

    setAddItem((PrevVal) =>{
      return [...PrevVal,note]
    })
  }

  const onDelete = (id) =>{
      setAddItem((oldItem) => {
        oldItem.filter( (current,index) =>{
           return index !==id
        })
      })
      console.log("delete")
  }
  return (
    <>
      <Header/>
      <CreateNote passNote={addNote} />
      {addItem.map((val,index)=>{
         return <Note
           key={index}
           id={index}
           title={val.title}
           content={val.content}
           deleteItem={onDelete}
         />

      })}
      <Footer/>
    </>
  );
}


export default App;
