import React, { useEffect, useState } from 'react';
const App = () => {
    const [num, setNum] = useState(0);
    const [nums,setNums] = useState(0);
    useEffect(() => {
        alert("clicked");
    })

    //USSEFECT HOOKS KA USE RENDER HONE KE BAD HOTA HAI ...MTLB RENDER HONE KE BD VI USEFFECT KAM KRTA HAI
    // jb jb render hota hai apne ap call hota hai useeffect apna yar
    return (
        <>
            <button onClick={() => {
                setNum(num + 1);
            }}>Click {num}</button>

            <button onClick={() => {
                setNums(nums + 1);
            }}>Click {nums}</button>

        </>

    );

}

export default App;
