import React, { useState } from 'react';

const App = () =>{
  let NewTime=new Date().toLocaleTimeString();
  const[Ctime,Stime] = useState(NewTime);
//USESTATE KE ANDAR EK CURRENT STATE AUR EK UPDATE STATE HOTA HAI ..
// CURRENT MAI SET KRO AND THAN UPDATE MAI VALUE CHANGE KRO JO AUTOMATIC ,, CURRENT KOM PASS HO JAYEGA 
  const Update = () =>{
    NewTime=new Date().toLocaleTimeString();
    Stime(NewTime);
  }
  return(
    <>
     <h1>{Ctime}</h1>
     <button onClick={Update}>Get time</button>
    </>
  );
}

export default App;
