import React, { useState } from 'react';


const App = () =>{
   let Newt = new Date().toLocaleTimeString();

   const[ctime,setCtime] = useState(Newt);

   const updateTime = () =>{
    Newt = new Date().toLocaleTimeString();
    setCtime(Newt);
   };

   setInterval(updateTime,1);
   return(
     <>
      <h1> {ctime} </h1>
     </>
   );
}

export default App;
