const App = () => {
  const purple = "#8e44ad";
  const [bg, changeBg] = useState(purple);

  // another usestate

  const[name,setName] = useState("Click me");

  const bgChange = () => {
    // console.log("yes");
    let newBg = "#34495e";
    changeBg(newBg);
    setName("ouch!! ğŸº ");
  
  }

  const bgBack = () =>{
    changeBg(purple);
    setName("Yuh!! ğŸ¤¸â€â™€ï¸");
  }

  return (
    <div style={{ backgroundColor: bg }}>
      <button onClick={bgChange}  onDoubleClick={bgBack}>{name}</button>
    </div>
  );
}

export default App;
